player_position: list[int] = [17, 2]
mobs_pos = [[14, 12], [11, 22], [4, 26], [6, 40], [23, 30], [38, 46], [32, 80], [18, 70], [19, 136]]
shop_pos = [23, 18]

def move_down():
    if map[player_position[0] + 1][player_position[1]] == 1:
        player_position[0] += 1
    elif map[player_position[0] + 1][player_position[1]] == 0:
        player_position[0] += 1
    elif map[player_position[0] + 1][player_position[1]] == 2:
        player_position[0] += 1

def move_up():
    if map[player_position[0] - 1][player_position[1]] == 1:
        player_position[0] -= 1
    elif map[player_position[0] - 1][player_position[1]] == 0:
        player_position[0] -= 1
    elif map[player_position[0] - 1][player_position[1]] == 2:
        player_position[0] -= 1

def move_right():
    if map[player_position[0]][player_position[1] + 2] == 1:
        player_position[1] += 2
    elif map[player_position[0]][player_position[1] + 2] == 0:
        player_position[1] += 2
    elif map[player_position[0]][player_position[1] + 2] == 2:
        player_position[1] += 2

def move_left():
    if map[player_position[0]][player_position[1] - 2] == 1:
        player_position[1] -= 2
    elif map[player_position[0]][player_position[1] - 2] == 0:
        player_position[1] -= 2
    elif map[player_position[0]][player_position[1] - 2] == 2:
        player_position[1] -= 2


def clear_screen():
    os.system('cls')
def redraw_map() -> str:
    output: str = ""
    for y, oszlop in enumerate(map):
        for x, value in enumerate(oszlop):
            if y == player_position[0] and x == player_position[1]:
                output += "¤"
                continue

            match value:
                case 20:
                    output += "█"
                case 26:
                    output += "▄"
                case 25:
                    output += "▀"
                case 1:
                    output += " "
                case 0:
                    output += "■"
                case 2:
                    output += "F"

        output += "\n"
    return output

def main():
    clear_screen()
    while True:
        if player_position in mobs_pos:
            mobs_pos.remove(player_position)
            map[player_position[0]][player_position[1]] = 1
            fighting()
        if player_position == shop_pos:
            shop()
        print(redraw_map(), end="")
        #print(player_position)
        direction: str = input("Console >")
        if direction == "help":
            print("Commands:\n"
                  "-turn on (Music)\n"
                  "-turn off (Music)\n"
                  "-skip (Music)\n"
                  "-a (balra)\n"
                  "-s (lefelé)\n"
                  "-w (felfelé)\n"
                  "-d (jobbra)\n")
            time.sleep(5)
        match direction:
            case "exit":
                clear_screen()
                exit()
            case "s":
                clear_screen()
                move_down()
            case "w":
                clear_screen()
                move_up()
            case "a":
                clear_screen()
                move_left()
            case "d":
                clear_screen()
                move_right()
            case "skip":
                clear_screen()
                skip()
            case "turn on":
                clear_screen()
                play_music()
            case "turn off":
                clear_screen()
                turn_off()
            case "inventory":
                inventory()
            case _:
                clear_screen()
                
